mapper.update:
UPDATE seller_tb
SET refresh_token = #{refreshToken}
WHERE uid = #{uid}

UPDATE seller_tb
store_name = #{req.storeName}, phone_number = #{req.phoneNumber}, address = #{req.address}, store_lat = #{req.storeLat}, store_lng = #{req.storeLng}, owner_name = #{req.ownerName}, business_uid = #{req.businessNumber}, store_open = #{req.storeOpen}, store_close = #{req.storeClose}, bank_name = #{req.bankName}, account_number = #{req.accountNumber}, account_holder = #{req.accountHolder}
WHERE uid = #{sellerUid}

mapper.select:
SELECT * FROM seller_tb
WHERE business_uid = #{businessUid}
AND phone_number = #{phoneNumber}
LIMIT 1
